sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {


    if (api.item.value.type == "reference") {
        return 'door-fill';
    } else {
        return 'document-template-outline';
    }
}
